import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const heading = {
  color: 'palevioletred',
  textAlign: 'center',
  textTransform: 'capitalize',
  fontWeight: 'bold',
  textShadow: '0px 2px 4px palegoldenrod'
};


let time = new Date().getHours();
let greet = "";

if ((time >= 1) && (time <= 11)) {
  greet = 'Good Morning';
} else if ((time >= 12) && (time <= 19)) {
  greet = 'Good Afternoon';
} else {
  greet = 'Good Night';
}

ReactDOM.render(
  <h1 style= {heading} >Hello Sir, {greet}</h1> , 
      document.getElementById("root"));
